import './components.css'
import React from 'react'

const liContainer = {
    width: "150px",
    height: "150px",
    margin: "20px",
    backgroundColor: "#ffff00",
    padding: "20px",
    overflow: "hidden",
    // overflowY: "scroll",
    borderRadius: "5px",
    boxShadow: "10px 10px 15px rgba(215, 215, 215, 0.8)",
}

const liContainerDone = {
    width: "150px",
    height: "150px",
    margin: "20px",
    backgroundColor: "#cccccc",
    padding: "20px",
    overflow: "hidden",
    // overflowY: "scroll",
    borderRadius: "5px",
    boxShadow: "10px 10px 15px rgba(215, 215, 215, 0.8)",
    textDecoration: "line-through",
    color: "rgb(0,0,0,0.4)"
}

const liInput = {
    width: "100%",
}

const contentAreaStyle = {
    display: "flex",
    flexDirection: "column",
    justifyContent: "space-between",
    height: "100%"
}

const textStyle = {
    wordWrap: "break-word"
}

const buttonGroupStyle = {
    // alignSelf: "flex-end"
    // textAlign: "center"
}

const tickButtonStyle = {
    padding: "5px 10px",
    backgroundColor: "rgb(0, 255, 0)",
    border: "0",
    cursor: "pointer",
    // width: "50px",
    textAlign: "center"
}

const ListItem = (props) => {

    // const handleClick = () => {
    //     this.setState((prevState) => ({ 
    //         editing: !prevState.editing
    //     }))
    // }

    const { listItem: {id, text, done, editing}, handleEditClick, handleDelete, handleTick, handleChange } = props

    return (
        <div style={done ? liContainerDone : liContainer}>
            { !editing &&
                <div style={contentAreaStyle}>
                    {/* <h1>Title</h1> */}
                    <p style={textStyle}>{text}</p>
                    <div style={buttonGroupStyle}>
                        <button style={tickButtonStyle} onClick={() => handleTick(id)}>DONE</button>
                        <button onClick={() => handleEditClick(id)}>edit</button>
                        <button onClick={() => handleDelete(id)}>delete</button>
                    </div>
                </div>
            }
            { editing &&
                <div style={contentAreaStyle}>
                    {/* <button onClick={() => handleEditClick(id)}>BACK</button> */}
                    <br/>
                    <input style={liInput} value={text} onChange={(e) => handleChange(id, e.target.value)}/>
                    <button onClick={() => handleEditClick(id)}>UPDATE</button>
                </div>
            }
        </div>
    )
    
}

export default ListItem
