import React, {Component} from 'react'
import Layout from '../components/Layout'
import ListItem from '../components/ListItem'
import Link from 'next/link'

class Listicles extends Component {
    state = {
        list: [
            {
                id: 0,
                text: "",
                done: false,
                editing: false
            }
        ],
        value: "",
        latestId: 0
    }

    componentDidUpdate(){
        console.log(this.state)
    }

    handlelistCreate = () => {
        this.setState(prevState => ({
            list: [
                ...prevState.list,
                {
                    id: prevState.latestId,
                    text: prevState.value,
                    done: false,
                    editing: false
                }
            ],
            latestId: prevState.latestId++
        }))
    }

    handleListCreateChange = (e) => {
        const {value} = e.target
        this.setState({ value })
    }

    handleListEditChange = () => {

    }

    render(){
        return (
            <div>
                <h1>Listicles</h1>
                <input
                    placeholder = "Enter a new todo here"
                    onChange = {this.handleListCreateChange}
                />
                <button onClick={this.handleListCreate}>ENTER</button>
                <ListItem/>
            </div>
        )
    }
}

export default Listicles
