import './components.css'
import React from 'react'

const liContainer = {
    width: "250px",
    height: "200px",
    margin: "20px",
    backgroundColor: "#cccccc",
    padding: "20px",
    overflowX: "hidden",
    overflowY: "scroll",
    borderRadius: "5px",
    boxShadow: "10px 10px 15px rgba(215, 215, 215, 0.8)"
}

class ListItem extends React.Component {
    state = {
        editing: false,
    }

    handleClick = () => {
        this.setState((prevState) => ({ 
            editing: !prevState.editing
        }))
    }

    render(){
        const { editing } = this.state
        return (
            <div style={liContainer}>
                { !editing &&
                    <div>
                        <h1>Title</h1>
                        <p>content content content content aeeeeeeeeeeeeeeeeeedddddddddddddddddddeeeeeeeeeeeeede vweeeeeeeeeeeeeeee ewwwwwwwwwS</p>
                        <div>
                            <button>tick</button>
                            <button onClick={this.handleClick}>edit</button>
                            <button>delete</button>
                        </div>
                    </div>
                }
                { editing &&
                    <div>
                        <button onClick={() => this.setState({ editing: true })}>BACK</button>
                        <br/>
                        <input/>
                    </div>
                }
            </div>
        )
    }
}

export default ListItem
