import './components.css'
import React from 'react'

const liContainer = {
    width: "150px",
    height: "150px",
    margin: "20px",
    backgroundColor: "#cccccc",
    padding: "20px",
    overflow: "hidden",
    // overflowY: "scroll",
    borderRadius: "5px",
    boxShadow: "10px 10px 15px rgba(215, 215, 215, 0.8)"
}

const liInput = {
    width: "100%",
}

const ListItem = (props) => {

    // const handleClick = () => {
    //     this.setState((prevState) => ({ 
    //         editing: !prevState.editing
    //     }))
    // }

    const { listItem: {id, text, done, editing}, handleEditClick, handleDelete } = props

    return (
        <div style={liContainer}>
            { !editing &&
                <div>
                    {/* <h1>Title</h1> */}
                    <p style={{wordWrap: "break-word"}}>{text}</p>
                    <div>
                        <button>tick</button>
                        <button onClick={() => handleEditClick(id)}>edit</button>
                        <button onClick={() => handleDelete(id)}>delete</button>
                    </div>
                </div>
            }
            { editing &&
                <div>
                    <button onClick={() => handleEditClick(id)}>BACK</button>
                    <br/>
                    <input style={liInput}/>
                    <button>DONE</button>
                </div>
            }
        </div>
    )
    
}

export default ListItem
